    public void cek(){
        kon=koneksi.koneksiDb();
        try {
            st=kon.createStatement();
            String sql = "select * from user where username ='"+usernm.getText()+"' and password='"+pass.getText()+"'";
            rs= st.executeQuery(sql);
            
            if(rs.next()){
                if(rs.getString("role").equals("Admin")){
                    formAdmin fa=new formAdmin();
                    fa.show();
                    this.dispose();
                }else if(rs.getString("role").equals("User")){
                    formUser fu=new formUser();
                    fu.show();
                    this.dispose();
                }else if(rs.getString("role").equals("Owner")){
                    formOwner fo=new formOwner();
                    fo.show();
                    this.dispose();
                }
            }else{ 
                JOptionPane.showMessageDialog(null,"Maaf Gagal masuk ID Pengguna atau Kata Sandi tidak sesuai");
                usernm.setText("");
                pass.setText("");
                usernm.requestFocus();
            }
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null,e);
        }
    }
